package com.collagemedia.kotlinexample.utilimport android.app.Activityimport android.util.DisplayMetrics////////////////////////////////////////////////////////////////////////////////// <!--                                                                        ///   ~ Copyright (c) 2016 by Gian An Bee (giaanbee@outllok.com)                ///   -->                                                                        ///                                                                             /////////////////////////////////////////////////////////////////////////////////object Config {    var SENT_PATH = "path_new"    var PATH_FOLDER = "/CatFaceEditor"    var FOLDER_NAME = "CatFaceEditor"    var ICON = "ecoticon/"    val GALLERY_PHOTO_ID_LIST = "photo_id_list"    val GALLERY_PHOTO_ORIENTATION_LIST = "photo_orientation_list"    val IS_SCRAP_BOOK = "is_scrap_book"    val COLLAGE_IMAGE_LIMIT = 9    var SCREENWIDTH: Int = 0    var SCREENHEIGHT: Int = 0    fun init(mActivity: Activity) {        val displaymetrics = DisplayMetrics()        mActivity.windowManager.defaultDisplay.getMetrics(displaymetrics)        SCREENWIDTH = displaymetrics.widthPixels        SCREENHEIGHT = displaymetrics.heightPixels    }}