package com.collagemedia.kotlinexample.utilimport android.app.Activityimport android.util.DisplayMetrics////////////////////////////////////////////////////////////////////////////////// <!--                                                                        ///   ~ Copyright (c) 2016 by Gian An Bee (giaanbee@outllok.com)                ///   -->                                                                        ///                                                                             /////////////////////////////////////////////////////////////////////////////////object Config {    var SCREENWIDTH: Int = 0    var SCREENHEIGHT: Int = 0    fun init(mActivity: Activity) {        val displaymetrics = DisplayMetrics()        mActivity.windowManager.defaultDisplay.getMetrics(displaymetrics)        SCREENWIDTH = displaymetrics.widthPixels        SCREENHEIGHT = displaymetrics.heightPixels    }}